mapper.insert:

insert into system_parameter (id, class_name, item, 
  param_value, user_modify, param_describe, 
  type, value_bound, show_class, 
  show_sub_class, gmt_create, gmt_modified
  )
values (#{id,jdbcType=INTEGER}, #{className,jdbcType=VARCHAR}, #{item,jdbcType=VARCHAR}, 
  #{paramValue,jdbcType=VARCHAR}, #{userModify,jdbcType=CHAR}, #{paramDescribe,jdbcType=VARCHAR}, 
  #{type,jdbcType=CHAR}, #{valueBound,jdbcType=VARCHAR}, #{showClass,jdbcType=VARCHAR}, 
  #{showSubClass,jdbcType=VARCHAR}, #{gmtCreate,jdbcType=TIMESTAMP}, #{gmtModified,jdbcType=TIMESTAMP}
  )

mapper.select:
select

from system_parameter
where  class_name = #{className,jdbcType=VARCHAR}
and item = #{item,jdbcType=VARCHAR}
